package com.amelie;import java.util.Scanner;public class Duel {    static Configuration configuration = new Configuration();    int nbEssais = configuration.getNbEssais();    Challenge challenge = new Challenge();    Defenseur defenseur = new Defenseur();    public void jeu() {        int l;        String compJoueur = "";        String compOrdi = "";        String reponse;        boolean victoireJoueur = false;        boolean victoireOrdi = false;        String mystJoueur = challenge.nbMystere();        String mystOrdi = defenseur.nbMystere();        String propOrdi = defenseur.proposition();        System.out.println("Proposition ordinateur : " + propOrdi);        for (l = 1; victoireJoueur == false && victoireOrdi == false && l <= nbEssais; l++) {            compOrdi = defenseur.compare(mystOrdi, propOrdi);            System.out.println("Reponse Ordinateur :" + compOrdi);            String propJoueur = challenge.proposition();            compJoueur = challenge.compare(mystJoueur, propJoueur);            System.out.println("Reponse Joueur :" + compJoueur);            if (compJoueur.equals("====")) {                victoireJoueur = true;            } else if (l < nbEssais) {                propOrdi = defenseur.ajuste(propOrdi, compOrdi);                System.out.println("Proposition Ordinateur :" + propOrdi);            }        }        if (victoireJoueur = true)            System.out.println("Bravo ! vous avez gagnÃ© !!!");        else            System.out.println("Dommage, vous avez perdu !!!");    }}